ALTER TABLE `message` ADD COLUMN `voiceId` varchar(30) COLLATE utf8mb4_general_ci NULL DEFAULT '' COMMENT '语音Id,为空为不使用' after `rows` , ADD COLUMN `isVoiced` tinyint(3) unsigned NOT NULL DEFAULT 0 COMMENT '语音是否接听' after `voiceId` , CHANGE `createdAt` `createdAt` timestamp NULL COMMENT '发送时间' after `isVoiced` , ADD KEY `voice`(`voiceId`);
ALTER TABLE `quote_batch` ADD COLUMN `isPhone` tinyint(3) unsigned NOT NULL DEFAULT 0 COMMENT '是否电话' after `total` , CHANGE `createdAt` `createdAt` timestamp NULL after `isPhone`;
ALTER TABLE `quote_batch_customer` ADD COLUMN `isPhone` tinyint(3) unsigned NOT NULL DEFAULT 0 COMMENT '是否电话' after `error` , CHANGE `createdAt` `createdAt` timestamp NULL COMMENT '创建时间' after `isPhone`;
ALTER TABLE `system_setting` CHANGE `allowedUsers` `allowedUsers` smallint (6) unsigned NOT NULL COMMENT '坐席数' after `tenantId`, CHANGE `dailySmsLimit` `dailySmsLimit` int (11) unsigned NOT NULL COMMENT '单日最大短信数' after `allowedUsers`, CHANGE `dailyVoiceLimit` `dailyVoiceLimit` int (10) unsigned NOT NULL COMMENT '单日最大语音次数' after `dailySmsLimit`;
CREATE TABLE `voice_recharge_log` ( `id` int(11) unsigned NOT NULL auto_increment, `tenantId` int(11) unsigned NULL COMMENT '租户', `quantity` int(10) unsigned NULL COMMENT '短息数量', `operatorType` enum('ADMIN','TENANT') COLLATE utf8mb4_general_ci NULL COMMENT '充值人类型', `operatorId` int(10) unsigned NOT NULL COMMENT '充值人，管理员ID或者用户Id', `remark` varchar(30) COLLATE utf8mb4_general_ci NULL COMMENT '充值备注', `createdAt` timestamp NULL, `updatedAt` timestamp NULL, PRIMARY KEY (`id`) ) ENGINE=InnoDB DEFAULT CHARSET='utf8mb4' COLLATE='utf8mb4_general_ci';
CREATE TABLE `voice_statistics` ( `id` int(11) unsigned NOT NULL auto_increment, `tenantId` int(11) unsigned NOT NULL, `code` varchar(50) COLLATE utf8mb4_general_ci NOT NULL COMMENT '接口模板代号', `times` tinyint(3) unsigned NOT NULL DEFAULT 1 COMMENT '发送次数', `remain` int(11) NULL COMMENT '剩余次数', `variables` varchar(128) COLLATE utf8mb4_general_ci NULL DEFAULT '' COMMENT '变量，逗号隔开', `createdAt` timestamp NULL on update CURRENT_TIMESTAMP, `updatedAt` timestamp NULL, PRIMARY KEY (`id`) ) ENGINE=InnoDB DEFAULT CHARSET='utf8mb4' COLLATE='utf8mb4_general_ci';
INSERT INTO `voice_statistics`(tenantId,CODE,times,remain,VARIABLES,createdAt,updatedAt) SELECT tenantId,'453662','1','0','{{车牌号}},{{车主姓名}}',createdAt,updatedAt FROM `message_statistics`;
